index 50ed8f0..290b82e 100644
--- a/io/io.cpp
+++ b/io/io.cpp
@@ -416,6 +416,21 @@ extern "C" int c_bufcache_status(natl *blocks, natl *io_count)
 //   ESAME 2023-01-11 )

 // ( SOLUZIONE 2023-01-11
+/**
+ * @brief Cerca un blocco nella buffer cache
+ * @param block numero del blocco da cercare
+ * @return puntatore al buffer se trovato, nullptr altrimenti
+ */
+buf_des* bufcache_find(natl block)
+{
+	des_ata *d = &hard_disk;
+	for (int i = 0; i < MAX_BUF_DES; i++) {
+		buf_des *b = &d->bufcache[i];
+		if (b->full && b->block == block)
+			return b;
+	}
+	return nullptr;
+}
 //   SOLUZIONE 2023-01-11 )
 /// Piedino dell'APIC per le richieste di interruzione dell'hard disk
 const natb HD_IRQ = 14;
@@ -495,6 +510,43 @@ extern "C" void c_readhd_n(natb vetti[], natl primo, natb quanti)

 /// @todo Gestire l'operazione tenendo conto della buffer cache.
 // ( SOLUZIONE 2023-01-11
+	sem_wait(d->mutex);
+	for (natb i = 0; i < quanti; i++) {
+		natl block = primo + i;
+		natb* dest = vetti + i * DIM_BLOCK;
+		
+		buf_des *b = bufcache_find(block);
+		
+		if (b) {
+			memcpy(dest, b->buf, DIM_BLOCK);
+			bufcache_promote(b);
+		} else {
+			buf_des *buf = nullptr;
+			int buf_idx;
+			
+			for (buf_idx = 0; buf_idx < MAX_BUF_DES; buf_idx++) {
+				if (!d->bufcache[buf_idx].full) {
+					buf = &d->bufcache[buf_idx];
+					break;
+				}
+			}
+			
+			if (!buf) {
+				buf_idx = d->lru;
+				buf = &d->bufcache[buf_idx];
+			}
+			
+			starthd_in(d, buf->buf, block, 1);
+			sem_wait(d->sincr);
+			
+			buf->full = true;
+			buf->block = block;
+			memcpy(dest, buf->buf, DIM_BLOCK);
+			
+			bufcache_promote(buf);
+		}
+	}
+	sem_signal(d->mutex);
 //   SOLUZIONE 2023-01-11 )
 //   ESAME 2023-01-11 ]
 }
@@ -543,6 +595,21 @@ extern "C" void c_writehd_n(natb vetto[], natl primo, natb quanti)
 	//sem_signal(d->mutex);
 /// @todo Gestire l'operazione tenendo conto della buffer cache.
 // ( SOLUZIONE 2023-01-11
+	sem_wait(d->mutex);
+	for (natb i = 0; i < quanti; i++) {
+		natl block = primo + i;
+		natb* src = vetto + i * DIM_BLOCK;
+		
+		starthd_out(d, src, block, 1);
+		sem_wait(d->sincr);
+		
+		buf_des *b = bufcache_find(block);
+		if (b) {
+			memcpy(b->buf, src, DIM_BLOCK);
+			bufcache_promote(b);
+		}
+	}
+	sem_signal(d->mutex);
 //   SOLUZIONE 2023-01-11 )
 //   ESAME 2023-01-11 ]
 }
@@ -735,6 +802,14 @@ bool hd_init()
 	hd::enable_intr();

 // ( SOLUZIONE 2023-01-11
+	for (int i = 0; i < MAX_BUF_DES; i++) {
+		d->bufcache[i].full = false;
+		d->bufcache[i].next = -1;
+		d->bufcache[i].prev = -1;
+	}
+	d->lru = -1;
+	d->mru = -1;
+	d->io_count = 0;
 //   SOLUZIONE 2023-01-11 )

 	return true;
