@@ -848,8 +867,34 @@ void copia_cow_condivisa(paddr dest)
 // dal processo corrente
 bool aggiorna_cow_privata(vaddr v)
 {
-// ( SOLUZIONE 2023-06-07
-//   SOLUZIONE 2023-06-07 )
+	if (v < ini_utn_w || v >= fin_utn_w)
+		return false;
+	
+	vaddr page = v & ~(DIM_PAGINA - 1);
+	paddr old_frame = trasforma(esecuzione->cr3, page);
+	if (old_frame == 0)
+		return false;
+	
+	paddr new_frame = alloca_frame();
+	if (new_frame == 0)
+		return false;
+	
+	memcpy(voidptr_cast(new_frame), voidptr_cast(old_frame), DIM_PAGINA);
+	
+	tab_iter it(esecuzione->cr3, page, DIM_PAGINA);
+	for (it.post(); it; it.next_post()) {
+		tab_entry& e = it.get_e();
+		int liv = it.get_l();
+		if (liv == 1) {
+			e = new_frame | BIT_P | BIT_US | BIT_RW;
+			break;
+		}
+	}
+	
+	asm volatile("sfence" ::: "memory");
+	asm volatile("invlpg (%0)" : : "r"(page) : "memory");
+	
+	return true;
 }

