diff --git a/io/io.cpp b/io/io.cpp
index 50ed8f0..41431b2 100644
--- a/io/io.cpp
+++ b/io/io.cpp
@@ -495,6 +495,61 @@ extern "C" void c_readhd_n(natb vetti[], natl primo, natb quanti)

 /// @todo Gestire l'operazione tenendo conto della buffer cache.
 // ( SOLUZIONE 2023-01-11
+	// Check if the block is already in the cache
+	int buf_idx = -1;
+	for (int i = 0; i < MAX_BUF_DES; i++) {
+		if (d->bufcache[i].full && d->bufcache[i].block == primo) {
+			buf_idx = i;
+			break;
+		}
+	}
+
+	if (buf_idx >= 0) {
+		// Cache hit: copy from cache and promote
+		buf_des *b = &d->bufcache[buf_idx];
+		memcpy(vetti, b->buf, quanti * DIM_BLOCK);
+		bufcache_promote(b);
+	} else {
+		// Cache miss: find a free buffer or use LRU
+		buf_des *b = nullptr;
+		
+		// Look for a free buffer
+		for (int i = 0; i < MAX_BUF_DES; i++) {
+			if (!d->bufcache[i].full) {
+				b = &d->bufcache[i];
+				break;
+			}
+		}
+		
+		// If no free buffer, use LRU
+		if (b == nullptr) {
+			b = &d->bufcache[d->lru];
+			// Remove from LRU list
+			if (b->next >= 0)
+				d->bufcache[b->next].prev = -1;
+			d->lru = b->next;
+			if (d->mru == b - d->bufcache)
+				d->mru = -1;
+			b->next = -1;
+			b->prev = -1;
+		}
+		
+		// Read from disk into buffer
+		sem_wait(d->mutex);
+		starthd_in(d, b->buf, primo, quanti);
+		sem_wait(d->sincr);
+		sem_signal(d->mutex);
+		
+		// Copy to user buffer
+		memcpy(vetti, b->buf, quanti * DIM_BLOCK);
+		
+		// Mark as full and set block number
+		b->full = true;
+		b->block = primo;
+		
+		// Add to LRU list as MRU
+		bufcache_promote(b);
+	}
 //   SOLUZIONE 2023-01-11 )
 //   ESAME 2023-01-11 ]
 }
@@ -543,6 +598,27 @@ extern "C" void c_writehd_n(natb vetto[], natl primo, natb quanti)
 	//sem_signal(d->mutex);
 /// @todo Gestire l'operazione tenendo conto della buffer cache.
 // ( SOLUZIONE 2023-01-11
+	// Always write to disk (write-through)
+	sem_wait(d->mutex);
+	starthd_out(d, vetto, primo, quanti);
+	sem_wait(d->sincr);
+	sem_signal(d->mutex);
+	
+	// Check if block is in cache
+	int buf_idx = -1;
+	for (int i = 0; i < MAX_BUF_DES; i++) {
+		if (d->bufcache[i].full && d->bufcache[i].block == primo) {
+			buf_idx = i;
+			break;
+		}
+	}
+	
+	// If in cache, update it and promote (no-allocate: don't add if not present)
+	if (buf_idx >= 0) {
+		buf_des *b = &d->bufcache[buf_idx];
+		memcpy(b->buf, vetto, quanti * DIM_BLOCK);
+		bufcache_promote(b);
+	}
 //   SOLUZIONE 2023-01-11 )
 //   ESAME 2023-01-11 ]
 }
@@ -735,6 +811,15 @@ bool hd_init()
 	hd::enable_intr();

 // ( SOLUZIONE 2023-01-11
+	// Initialize buffer cache
+	d->lru = -1;
+	d->mru = -1;
+	d->io_count = 0;
+	for (int i = 0; i < MAX_BUF_DES; i++) {
+		d->bufcache[i].full = false;
+		d->bufcache[i].next = -1;
+		d->bufcache[i].prev = -1;
+	}
 //   SOLUZIONE 2023-01-11 )

 	return true;
