+/**
+ * @brief Cerca un blocco nella buffer cache
+ * @param block numero del blocco da cercare
+ * @return puntatore al buffer se trovato, nullptr altrimenti
+ */
+buf_des* bufcache_find(natl block)
+{
+	des_ata *d = &hard_disk;
+	for (int i = 0; i < MAX_BUF_DES; i++) {
+		buf_des *b = &d->bufcache[i];
+		if (b->full && b->block == block)
+			return b;
+	}
+	return nullptr;
+}
