+					buf = &d->bufcache[buf_idx];
+					break;
+				}
+			}
+			
+			if (!buf) {
+				buf_idx = d->lru;
+				buf = &d->bufcache[buf_idx];
+			}
+			
+			starthd_in(d, buf->buf, block, 1);
+			sem_wait(d->sincr);
+			
+			buf->full = true;
+			buf->block = block;
+			memcpy(dest, buf->buf, DIM_BLOCK);
+			
+			bufcache_promote(buf);
+		}
+	}
+	sem_signal(d->mutex);
 //   SOLUZIONE 2023-01-11 )
 //   ESAME 2023-01-11 ]
 }
